import React, { useState, useEffect } from 'react';
import Axios from 'axios';
import './Login.css'
import { Link } from "react-router-dom";
import {useHistory} from 'react-router-dom';

const RoomDetails = () => {
    const [roomDetails, setRoomDetails] = useState([]);
  
    const fetchRoomDetails = () => {
      Axios.get("http://localhost:8082/api/v1/user-profile").then(res => {
        console.log(res)
        setRoomDetails(res.data)
      });
    };
  
    useEffect(() => {
      fetchRoomDetails();
    }, []);

    return roomDetails.map((roomDetail, index) => {
        return (roomDetail);
    });
  };

const PinInputTag = () => {
    const getInputValue = (event)=>{
        const userValue = event.target.value;
    };

    return (
        <input type='password' placeholder='PIN' onChange={getInputValue} />
    )
}

const RoomInputTag = () => {
    const getInputValue = (event)=>{
        const userValue = event.target.value;
    };

    return (
        <input type='password' placeholder='Room number' onChange={getInputValue} />
    )
}

    if(RoomInputTag.getInputValue == RoomDetails.username 
        & PinInputTag.getInputValue == RoomDetails.password) {
                let history = useHistory();
                history.push('/Home');  
    }

function Login() {
    return (
        <div className='login'>
            <Link to='/'>
                <img
                    className="login__logo"
                    src='https://gardenhotel.kg/static/img/logo.png' 
                />
            </Link>

            <div className='login__container'>
                <h1>Sign-in</h1>

                <form>
                    <h5>PIN code</h5>
                    <PinInputTag />

                    <h5>Room number</h5>
                    <RoomInputTag />

                    <button type='submit' className='login__signInButton'>Sign In</button>
                </form>
            </div>
            <div className='footer'>
                    
            </div>
        </div>
        )
    }
    
export default Login
