import React, { useState, useEffect } from 'react';
import Axios from 'axios';
import './Login.css'
import { Link } from "react-router-dom";
import {useHistory} from 'react-router-dom';

const PinInputTag = () => {
    const getInputValue = (event)=>{
        const userValue = event.target.value;
    };

    return (
        <input type='password' placeholder='PIN' onChange={getInputValue} />
    )
}

const RoomInputTag = () => {
    const getInputValue = (event)=>{
        const userValue = event.target.value;
    };

    return (
        <input type='text' placeholder='Room number' onChange={getInputValue} />
    )
}

const SendData = () => {
    var bodyFormData = new FormData();
    bodyFormData.append(RoomInputTag.getInputValue, PinInputTag.getInputValue)
    console.log(bodyFormData)
    Axios({
        method: "post",
        url: "myurl",
        data: bodyFormData,
        headers: { "Content-Type": "multipart/form-data" },
      })
        .then(function (response) {
            this.props.history.push('/Home');
          console.log(response);
        })
        .catch(function (error) {
          //handle error
          console.log(error);
        });
}

function Login() {
    return (
        <div className='login'>
            <Link to='/'>
                <img
                    className="login__logo"
                    src='https://gardenhotel.kg/static/img/logo.png' 
                />
            </Link>

            <div className='login__container'>
                <h1>Sign-in</h1>

                <form>
                    <h5>PIN code</h5>
                    <PinInputTag />

                    <h5>Room number</h5>
                    <RoomInputTag />

                    <button type='submit' onSubmit={SendData} className='login__signInButton'>Sign In</button>
                </form>
            </div>
            <div className='footer'>
                    
            </div>
        </div>
        )
    }
    
export default Login
